    ASSERT_TRUE(fAllInputsInMempool);
    ASSERT_TRUE(mempool.size() == vHexNotaInputsTxes.size());

    CTransaction notaTx;
    ASSERT_TRUE(DecodeHexTx(notaTx, strOtherMARTYKMDNotaHex));

